import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useQuery } from 'react-query';
import { 
  Users, 
  CreditCard, 
  TrendingUp, 
  Plus,
  ArrowRight,
  Calendar,
  DollarSign,
  AlertCircle
} from 'lucide-react';
import { useAuthStore } from '../stores/authStore';
import api, { endpoints } from '../services/api';
import toast from 'react-hot-toast';

const Dashboard = () => {
  const { user } = useAuthStore();
  const [quickStats, setQuickStats] = useState({
    totalGroups: 0,
    totalContributions: 0,
    pendingPayments: 0,
    nextPayout: null,
  });

  // Fetch user's groups
  const { data: groupsData, isLoading: groupsLoading } = useQuery(
    'userGroups',
    () => api.get(endpoints.groups.myGroups),
    {
      onSuccess: (data) => {
        setQuickStats(prev => ({
          ...prev,
          totalGroups: data.data.groups?.length || 0,
        }));
      },
    }
  );

  // Fetch recent transactions
  const { data: transactionsData, isLoading: transactionsLoading } = useQuery(
    'recentTransactions',
    () => api.get(endpoints.transactions.myTransactions),
    {
      onSuccess: (data) => {
        const transactions = data.data.transactions || [];
        const totalContributions = transactions
          .filter(t => t.type === 'contribution' && t.status === 'completed')
          .reduce((sum, t) => sum + t.amount, 0);

        setQuickStats(prev => ({
          ...prev,
          totalContributions,
        }));
      },
    }
  );

  // Fetch recent payments
  const { data: paymentsData, isLoading: paymentsLoading } = useQuery(
    'recentPayments',
    () => api.get(endpoints.payments.myPayments),
    {
      onSuccess: (data) => {
        const payments = data.data.payments || [];
        const pendingPayments = payments
          .filter(p => p.status === 'pending')
          .reduce((sum, p) => sum + p.amount, 0);

        setQuickStats(prev => ({
          ...prev,
          pendingPayments,
        }));
      },
    }
  );

  const isLoading = groupsLoading || transactionsLoading || paymentsLoading;

  const getGreeting = () => {
    const hour = new Date().getHours();
    if (hour < 12) return 'Good morning';
    if (hour < 17) return 'Good afternoon';
    return 'Good evening';
  };

  const getRoleDisplay = (role) => {
    switch (role) {
      case 'kiongozi':
        return 'Group Leader';
      case 'member':
        return 'Group Member';
      case 'admin':
        return 'Administrator';
      default:
        return role;
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'active':
        return 'bg-success-100 text-success-800';
      case 'pending':
        return 'bg-warning-100 text-warning-800';
      case 'completed':
        return 'bg-primary-100 text-primary-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  if (isLoading) {
    return (
      <div className="space-y-6">
        <div className="animate-pulse">
          <div className="h-8 bg-gray-200 rounded w-1/4 mb-4"></div>
          <div className="h-4 bg-gray-200 rounded w-1/2"></div>
        </div>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
          {[...Array(4)].map((_, i) => (
            <div key={i} className="card animate-pulse">
              <div className="h-4 bg-gray-200 rounded w-1/2 mb-2"></div>
              <div className="h-8 bg-gray-200 rounded w-1/3"></div>
            </div>
          ))}
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Welcome Header */}
      <div className="bg-gradient-to-r from-primary-600 to-primary-700 rounded-2xl p-6 text-white">
        <h1 className="text-2xl font-bold mb-2">
          {getGreeting()}, {user?.name}! ðŸ‘‹
        </h1>
        <p className="text-primary-100">
          Welcome back to your Kijumbe dashboard. Here's what's happening with your savings groups.
        </p>
        <div className="mt-4 inline-flex items-center px-3 py-1 bg-white/20 rounded-full text-sm">
          <span className="capitalize">{getRoleDisplay(user?.role)}</span>
        </div>
      </div>

      {/* Quick Stats */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <div className="card">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Total Groups</p>
              <p className="text-2xl font-bold text-gray-900">{quickStats.totalGroups}</p>
            </div>
            <div className="h-12 w-12 bg-primary-100 rounded-lg flex items-center justify-center">
              <Users className="h-6 w-6 text-primary-600" />
            </div>
          </div>
        </div>

        <div className="card">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Total Contributions</p>
              <p className="text-2xl font-bold text-gray-900">
                {quickStats.totalContributions.toLocaleString()} TZS
              </p>
            </div>
            <div className="h-12 w-12 bg-success-100 rounded-lg flex items-center justify-center">
              <TrendingUp className="h-6 w-6 text-success-600" />
            </div>
          </div>
        </div>

        <div className="card">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Pending Payments</p>
              <p className="text-2xl font-bold text-gray-900">
                {quickStats.pendingPayments.toLocaleString()} TZS
              </p>
            </div>
            <div className="h-12 w-12 bg-warning-100 rounded-lg flex items-center justify-center">
              <CreditCard className="h-6 w-6 text-warning-600" />
            </div>
          </div>
        </div>

        <div className="card">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Next Payout</p>
              <p className="text-2xl font-bold text-gray-900">
                {quickStats.nextPayout ? 'Soon' : 'N/A'}
              </p>
            </div>
            <div className="h-12 w-12 bg-info-100 rounded-lg flex items-center justify-center">
              <Calendar className="h-6 w-6 text-info-600" />
            </div>
          </div>
        </div>
      </div>

      {/* Quick Actions */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        <div className="card">
          <h3 className="text-lg font-semibold text-gray-900 mb-4">Quick Actions</h3>
          <div className="space-y-3">
            {user?.role === 'kiongozi' && (
              <Link
                to="/groups"
                className="flex items-center justify-between p-3 bg-primary-50 hover:bg-primary-100 rounded-lg transition-colors group"
              >
                <div className="flex items-center space-x-3">
                  <div className="h-8 w-8 bg-primary-600 rounded-lg flex items-center justify-center">
                    <Plus className="h-4 w-4 text-white" />
                  </div>
                  <span className="font-medium text-gray-900">Create New Group</span>
                </div>
                <ArrowRight className="h-4 w-4 text-gray-400 group-hover:text-primary-600 transition-colors" />
              </Link>
            )}

            <Link
              to="/groups"
              className="flex items-center justify-between p-3 bg-gray-50 hover:bg-gray-100 rounded-lg transition-colors group"
            >
              <div className="flex items-center space-x-3">
                <div className="h-8 w-8 bg-gray-600 rounded-lg flex items-center justify-center">
                  <Users className="h-4 w-4 text-white" />
                </div>
                <span className="font-medium text-gray-900">View My Groups</span>
              </div>
              <ArrowRight className="h-4 w-4 text-gray-400 group-hover:text-gray-600 transition-colors" />
            </Link>

            <Link
              to="/payments"
              className="flex items-center justify-between p-3 bg-gray-50 hover:bg-gray-100 rounded-lg transition-colors group"
            >
              <div className="flex items-center space-x-3">
                <div className="h-8 w-8 bg-gray-600 rounded-lg flex items-center justify-center">
                  <CreditCard className="h-4 w-4 text-white" />
                </div>
                <span className="font-medium text-gray-900">Make Payment</span>
              </div>
              <ArrowRight className="h-4 w-4 text-gray-400 group-hover:text-gray-600 transition-colors" />
            </Link>
          </div>
        </div>

        <div className="card">
          <h3 className="text-lg font-semibold text-gray-900 mb-4">Recent Activity</h3>
          <div className="space-y-3">
            {transactionsData?.data?.transactions?.slice(0, 3).map((transaction) => (
              <div key={transaction.$id} className="flex items-center space-x-3 p-3 bg-gray-50 rounded-lg">
                <div className={`h-8 w-8 rounded-lg flex items-center justify-center ${
                  transaction.type === 'contribution' ? 'bg-success-100' : 'bg-primary-100'
                }`}>
                  {transaction.type === 'contribution' ? (
                    <DollarSign className="h-4 w-4 text-success-600" />
                  ) : (
                    <TrendingUp className="h-4 w-4 text-primary-600" />
                  )}
                </div>
                <div className="flex-1 min-w-0">
                  <p className="text-sm font-medium text-gray-900 capitalize">
                    {transaction.type}
                  </p>
                  <p className="text-xs text-gray-500">
                    {new Date(transaction.created_at).toLocaleDateString()}
                  </p>
                </div>
                <div className="text-right">
                  <p className="text-sm font-semibold text-gray-900">
                    {transaction.amount.toLocaleString()} TZS
                  </p>
                  <span className={`inline-flex items-center px-2 py-1 rounded-full text-xs font-medium ${getStatusColor(transaction.status)}`}>
                    {transaction.status}
                  </span>
                </div>
              </div>
            ))}

            {(!transactionsData?.data?.transactions || transactionsData.data.transactions.length === 0) && (
              <div className="text-center py-6 text-gray-500">
                <AlertCircle className="h-8 w-8 mx-auto mb-2 text-gray-400" />
                <p>No recent activity</p>
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Groups Overview */}
      {groupsData?.data?.groups && groupsData.data.groups.length > 0 && (
        <div className="card">
          <div className="flex items-center justify-between mb-4">
            <h3 className="text-lg font-semibold text-gray-900">My Groups</h3>
            <Link
              to="/groups"
              className="text-primary-600 hover:text-primary-500 text-sm font-medium"
            >
              View all
            </Link>
          </div>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {groupsData.data.groups.slice(0, 3).map((group) => (
              <div key={group.$id} className="p-4 border border-gray-200 rounded-lg hover:border-primary-300 transition-colors">
                <div className="flex items-center justify-between mb-2">
                  <h4 className="font-medium text-gray-900">{group.name}</h4>
                  <span className={`inline-flex items-center px-2 py-1 rounded-full text-xs font-medium ${
                    group.role === 'kiongozi' ? 'bg-primary-100 text-primary-800' : 'bg-gray-100 text-gray-800'
                  }`}>
                    {group.role === 'kiongozi' ? 'Leader' : 'Member'}
                  </span>
                </div>
                <div className="space-y-1 text-sm text-gray-600">
                  <p>Contribution: {group.contribution_amount?.toLocaleString()} TZS</p>
                  <p>Members: {group.max_members}</p>
                  <p>Duration: {group.rotation_duration} months</p>
                </div>
                <Link
                  to={`/groups/${group.$id}`}
                  className="mt-3 inline-flex items-center text-primary-600 hover:text-primary-500 text-sm font-medium"
                >
                  View details
                  <ArrowRight className="h-4 w-4 ml-1" />
                </Link>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* WhatsApp Integration Info */}
      <div className="bg-gradient-to-r from-green-600 to-green-700 rounded-2xl p-6 text-white">
        <div className="flex items-center justify-between">
          <div>
            <h3 className="text-lg font-semibold mb-2">ðŸ“± WhatsApp Integration</h3>
            <p className="text-green-100 mb-3">
              Get updates and manage your savings directly on WhatsApp! Send "HELP" to our bot to get started.
            </p>
            <div className="flex items-center space-x-2 text-sm">
              <span className="bg-white/20 px-2 py-1 rounded">Status: Active</span>
              <span className="bg-white/20 px-2 py-1 rounded">Bot: +255 XXX XXX XXX</span>
            </div>
          </div>
          <div className="text-right">
            <div className="text-3xl mb-2">ðŸ’¬</div>
            <p className="text-sm text-green-100">Available 24/7</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
