import axios from 'axios';

// Create axios instance
const api = axios.create({
  baseURL: process.env.REACT_APP_API_URL || 'http://localhost:5000/api',
  timeout: 10000,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Request interceptor to add auth token
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('auth-storage');
    if (token) {
      try {
        const authData = JSON.parse(token);
        if (authData.state?.token) {
          config.headers.Authorization = `Bearer ${authData.state.token}`;
        }
      } catch (error) {
        console.error('Error parsing auth token:', error);
      }
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// Response interceptor to handle errors
api.interceptors.response.use(
  (response) => {
    return response;
  },
  (error) => {
    if (error.response?.status === 401) {
      // Token expired or invalid, redirect to login
      localStorage.removeItem('auth-storage');
      window.location.href = '/login';
    }
    return Promise.reject(error);
  }
);

// API endpoints
export const endpoints = {
  // Auth
  auth: {
    login: '/auth/login',
    register: '/auth/register',
    profile: '/auth/profile',
    logout: '/auth/logout',
  },

  // Groups
  groups: {
    create: '/groups/create',
    join: '/groups/join',
    myGroups: '/groups/my-groups',
    details: (id) => `/groups/${id}`,
    update: (id) => `/groups/${id}`,
    close: (id) => `/groups/${id}/close`,
  },

  // Members
  members: {
    groupMembers: (groupId) => `/members/group/${groupId}`,
    profile: (id) => `/members/profile/${id}`,
    statistics: (id) => `/members/statistics/${id}`,
    payments: (id) => `/members/payments/${id}`,
    rotation: (id) => `/members/rotation/${id}`,
    update: (id) => `/members/${id}`,
    remove: (id) => `/members/${id}`,
  },

  // Transactions
  transactions: {
    create: '/transactions/create',
    groupTransactions: (groupId) => `/transactions/group/${groupId}`,
    myTransactions: '/transactions/my-transactions',
    summary: (groupId) => `/transactions/group/${groupId}/summary`,
    updateStatus: (id) => `/transactions/${id}/status`,
  },

  // Payments
  payments: {
    create: '/payments/create',
    status: (id) => `/payments/status/${id}`,
    myPayments: '/payments/my-payments',
    groupPayments: (groupId) => `/payments/group/${groupId}`,
  },

  // Overdrafts
  overdrafts: {
    request: '/overdrafts/request',
    myOverdrafts: '/overdrafts/my-overdrafts',
    groupOverdrafts: (groupId) => `/overdrafts/group/${groupId}`,
    details: (id) => `/overdrafts/${id}`,
    update: (id) => `/overdrafts/${id}`,
    repay: (id) => `/overdrafts/${id}/repay`,
  },

  // WhatsApp
  whatsapp: {
    status: '/whatsapp/status',
    sendNotification: '/whatsapp/send-notification',
  },

  // Admin
  admin: {
    users: '/admin/users',
    userDetails: (id) => `/admin/users/${id}`,
    updateUser: (id) => `/admin/users/${id}`,
    groups: '/admin/groups',
    groupDetails: (id) => `/admin/groups/${id}`,
    createGroup: '/admin/groups',
    updateGroup: (id) => `/admin/groups/${id}`,
    statistics: '/admin/statistics',
    recentActivity: '/admin/recent-activity',
  },
};

export default api;
