import axios from 'axios';

// Create axios instance
const api = axios.create({
  baseURL: process.env.REACT_APP_API_URL || '/backend/',
  timeout: 10000,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Request interceptor to add auth token
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }

    // Log the request for debugging
    console.log('API Request:', config.method?.toUpperCase(), config.url);

    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// Response interceptor to handle auth errors
api.interceptors.response.use(
  (response) => {
    return response;
  },
  (error) => {
    console.error('API Error:', error.response?.status, error.response?.data);

    if (error.response?.status === 401) {
      // Token expired or invalid, redirect to login
      localStorage.removeItem('token');
      window.location.href = '/login';
    }
    return Promise.reject(error);
  }
);

// API endpoints
export const endpoints = {
  // Auth
  auth: {
    login: '/auth/login',
    register: '/auth/register',
    profile: '/auth/profile',
    logout: '/auth/logout',
  },

  // Groups
  groups: {
    create: '/groups/create',
    join: '/groups/join',
    myGroups: '/groups/my-groups',
    details: (id) => `/groups/${id}`,
    close: (id) => `/groups/${id}/close`,
  },

  // Members
  members: {
    groupMembers: (groupId) => `/members/group/${groupId}`,
    memberDetails: (id) => `/members/${id}`,
    memberProfile: (id) => `/members/profile/${id}`,
    memberStats: (id) => `/members/statistics/${id}`,
    memberPayments: (id) => `/members/payments/${id}`,
    memberRotation: (id) => `/members/rotation/${id}`,
  },

  // Transactions
  transactions: {
    create: '/transactions/create',
    groupTransactions: (groupId) => `/transactions/group/${groupId}`,
    myTransactions: '/transactions/my-transactions',
    updateStatus: (id) => `/transactions/${id}/status`,
    groupSummary: (groupId) => `/transactions/group/${groupId}/summary`,
  },

  // Payments
  payments: {
    create: '/payments/create',
    callback: '/payments/callback',
    cancel: '/payments/cancel',
    status: (id) => `/payments/status/${id}`,
    myPayments: '/payments/my-payments',
    groupPayments: (groupId) => `/payments/group/${groupId}`,
  },

  // Overdrafts
  overdrafts: {
    request: '/overdrafts/request',
    myOverdrafts: '/overdrafts/my-overdrafts',
    groupOverdrafts: (groupId) => `/overdrafts/group/${groupId}`,
    details: (id) => `/overdrafts/${id}`,
    repay: (id) => `/overdrafts/${id}/repay`,
  },

  // WhatsApp
  whatsapp: {
    webhook: '/whatsapp/webhook',
    sendNotification: '/whatsapp/send-notification',
    status: '/whatsapp/status',
  },

  // Admin
  admin: {
    users: '/admin/users',
    userDetails: (id) => `/admin/users/${id}`,
    groups: '/admin/groups',
    groupDetails: (id) => `/admin/groups/${id}`,
    statistics: '/admin/statistics',
    recentActivity: '/admin/recent-activity',
  },
};

export default api;
